var btn=document.querySelector(".js-btn"),popup=document.querySelector(".modal-content"),form=popup.querySelector("form"),arrival=popup.querySelector("[name=arrival-date]"),departure=popup.querySelector("[name=date-of-departure]"),adult=popup.querySelector("[name=adult]"),child=popup.querySelector("[name=child]");btn.addEventListener("click",function(a){a.preventDefault(),popup.classList.add("modal-content-show")}),form.addEventListener("submit",function(a){arrival.value&&departure.value&&adult.value&&child.value||a.preventDefault()}),window.addEventListener("keydown",function(a){27===a.keyCode&&popup.classList.contains("modal-content-show")&&popup.classList.remove("modal-content-show")});